import React, { useState } from "react";
import data from "../../data/index.json";
import "../../styles/skills.css";

export default function MySkills() {
  const [selectedSkill, setSelectedSkill] = useState(null);

  const handleSkillClick = (index) => {
    setSelectedSkill(index);
  };

  return (
    <section className="skills-section" id="mySkills">
      <div className="row">
        <div className="col">
          <h3>{data.skills[0]?.title}</h3>
        </div>
        <div className="col">
          <h3>{data.skills[1]?.title}</h3>
        </div>
      </div>
      <div className="row">
        <div className="col" onClick={() => handleSkillClick(0)}>
          <h3>{data.skills[0]?.title}</h3>
          {selectedSkill === 0 && <p>{data.skills[0]?.description}</p>}
        </div>
        <div className="col" onClick={() => handleSkillClick(1)}>
          <h3>{data.skills[1]?.title}</h3>
          {selectedSkill === 1 && <p>{data.skills[1]?.description}</p>}
        </div>
      </div>
    </section>
  );
}
