import React, { useState } from 'react';
import "../../styles/honors.css";

function HonorsAwards() {
  const [scrollPosition, setScrollPosition] = useState(0);

  const scrollLeft = () => {
    setScrollPosition(Math.max(scrollPosition - 1, 0));
  };

  const scrollRight = () => {
    // Assuming each block contains 2 items
    const totalBlocks = Math.ceil(honorsData.length / 2);
    setScrollPosition(Math.min(scrollPosition + 1, totalBlocks - 1));
  };

  const honorsData = [
    "100% Scholarship for undergraduate studies",
    "College Chess Player Award",
    "Winner of the 2019 Coding Competition",
    "Volunteered for NSS events in college"
    // Add more honors and awards here
  ];

  return (
    <section id="honorsAwards" className="honors-awards">
      <div className="container">
        <h2 className="section-heading">Honors and Awards</h2>
        <div className="scrollable-honors">
          <ul className="honors-awards-list" style={{ transform: `translateX(-${scrollPosition * 50}%)` }}>
            {honorsData.map((item, index) => (
              <li key={index} className="honors-awards-item">{item}</li>
            ))}
          </ul>
        </div>
        <button className="scroll-left" onClick={scrollLeft}>&#8592;</button>
        <button className="scroll-right" onClick={scrollRight}>&#8594;</button>
      </div>
    </section>
  );
}

export default HonorsAwards;
