import React, { useState, useEffect } from "react";
import TrackVisibility from 'react-on-screen';

const HeroSection = ({ jobTitles = ["Student", "Developer"] }) => {
  const [text, setText] = useState(jobTitles[0]);
  const [index, setIndex] = useState(0);
  const period = 2000;

  useEffect(() => {
    const interval = setInterval(() => {
      tick();
    }, period);
    return () => clearInterval(interval);
  }, [index]);

  const tick = () => {
    setIndex(prevIndex => (prevIndex + 1) % jobTitles.length);
    setText(jobTitles[index]);
  };

  return (
    <section id="heroSection" className="hero-section">
      <div className="hero-section-content-box">
        <div className="hero-section-content">
          <p className="section-title">Hey, I'm Ajay Kumar Medikonda</p>
          <h1 className="hero-section-title">
            <span className="hero-section-title-color">{text}</span> <br />
          </h1>
          <p className="hero-section-description">
            Developer
          </p>
        </div>
        <a href="#Contact" className="btn btn-primary">Get In Touch</a>
      </div>
      <div className="hero-section-img">
        <TrackVisibility>
          {({ isVisible }) => (
            <div className={isVisible ? "animate__animated animate__zoomIn" : ""}>
              <img src="./img/hero_img.png" alt="Hero Section" />
            </div>
          )}
        </TrackVisibility>
      </div>
    </section>
  );
}

export default HeroSection;
