import React from "react";
import data from "../../data/index.json";
import "../../styles/skills.css";

export default function MySkills() {
  const handleSkillClick = (index) => {
    const description = data.skills[index].description;
    document.getElementById('descriptionColumn').innerHTML = '<p>' + description + '</p>';
    document.getElementById('descriptionColumn').style.display = 'block';
  };

  return (
    <section className="skills-section" id="mySkills">
      <div className="skills-column">
        {data?.skills?.map((skill, index) => (
          <div key={index} className="skill-card" onClick={() => handleSkillClick(index)}>
            <h3>{skill.title}</h3>
          </div>
        ))}
      </div>
      <div className="description-column" id="descriptionColumn">
        <p>Select a skill to see its description.</p>
      </div>
    </section>
  );
}
