// about.jsx
import React, { useEffect, useRef } from "react";
import "../../styles/about.css";

function AboutMe() {
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    const items = container.querySelectorAll(".info-item");

    // Calculate rotation angle for each item
    const angle = (2 * Math.PI) / items.length;
    let currentAngle = 0;

    // Rotate each item around the center
    items.forEach((item, index) => {
      const radius = 100; // Adjust the radius as needed
      const centerX = container.offsetWidth / 2;
      const centerY = container.offsetHeight / 2;
      const x = centerX + radius * Math.cos(currentAngle);
      const y = centerY + radius * Math.sin(currentAngle);

      // Apply position
      item.style.left = `${x}px`;
      item.style.top = `${y}px`;

      currentAngle += angle;
    });
  }, []);

  return (
    <section id="AboutMe" className="about-section">
      <div className="about-section-content" ref={containerRef}>
        <div className="about-section-img">
          <img src="./img/about-me.png" alt="About Me" />
        </div>
        <div className="info-item">
          <span className="info-text">I am a web developer</span>
        </div>
        <div className="info-item">
          <span className="info-text">Passionate about coding</span>
        </div>
        <div className="info-item">
          <span className="info-text">Love learning new technologies</span>
        </div>
        <div className="info-item">
          <span className="info-text">Enjoy problem-solving</span>
        </div>
        <div className="info-item">
          <span className="info-text">Dedicated to creating great user experiences</span>
        </div>
      </div>
    </section>
  );
}

export default AboutMe;
