import React, { useRef, useEffect } from "react";
import "../../styles/about.css";

function AboutMe() {
  const imgRef = useRef(null);

  useEffect(() => {
    const img = imgRef.current;
    const imgRect = img.getBoundingClientRect();
    const centerX = imgRect.left + imgRect.width / 2;
    const centerY = imgRect.top + imgRect.height / 2;
    const radiusX = imgRect.width / 2;
    const radiusY = imgRect.height / 2;

    // Calculate the position of vertices
    const vertices = [
      { x: centerX + radiusX, y: centerY }, // Right vertex
      { x: centerX, y: centerY + radiusY }, // Bottom vertex
      { x: centerX - radiusX, y: centerY }, // Left vertex
      { x: centerX, y: centerY - radiusY } // Top vertex
    ];

    console.log("Vertices:", vertices);

    // Position circles around the image based on vertices
    const circles = document.querySelectorAll(".circle");
    circles.forEach((circle, index) => {
      const vertex = vertices[index % 4];
      circle.style.left = `${vertex.x}px`;
      circle.style.top = `${vertex.y}px`;
    });
  }, []);

  return (
    <section id="AboutMe" className="about-section">
      <div className="about-section-content">
        <div className="about-section-left">
          <img
            ref={imgRef}
            src="/img/about-me.png"
            alt="About Me"
            className="about-section-img"
          />
        </div>
        <div className="about-section-right">
          <div className="circle-container">
            <div className="circle">Web Developer</div>
            <div className="circle">Passionate Coder</div>
            <div className="circle">Tech Enthusiast</div>
            <div className="circle">Problem Solver</div>
            <div className="circle">UX Designer</div>
            <div className="circle">Creative Thinker</div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default AboutMe;
